INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (19920, 6445, 'Propranolol', 'Pheochromocytoma', 'Administration of beta-blockers alone in the setting of pheochromocytoma has been associated with a paradoxical increase in blood pressure due to the attenuation of beta-mediated vasodilatation in skeletal muscle.  In patients with pheochromocytoma, an alpha-blocking agent should be initiated prior to the use of any beta-blocking agent.  Caution should be taken in the administration of these agents to patients suspected of having pheochromocytoma.', '2', '"Product Information. Sectral (acebutolol)." Wyeth-Ayerst Laboratories  (2002):|"Product Information. Tenormin (atenolol)." ICN Pharmaceuticals Inc  (2002):|"Product Information. Trandate (labetalol)." Glaxo Wellcome  (2002):|"Product Information. Corgard (nadolol)." Bristol-Myers Squibb  (2002):|"Product Information. Inderal (propranolol)." Wyeth-Ayerst Laboratories  (2001):|"Product Information. Blocadren (timolol)." Merck & Co., Inc  (2001):|"Product Information. Brevibloc (esmolol)." DuPont Pharmaceuticals  (2001):|"Product Information. Cartrol (carteolol)." Abbott Pharmaceutical  (2001):|"Product Information. Kerlone (betaxolol)." Searle  (2001):|"Product Information. Lopressor (metoprolol)." Novartis Pharmaceuticals  (2001):|"Product Information. Betapace (sotalol)." Berlex Laboratories  (2001):|"Product Information. Zebeta (bisoprolol)." Lederle Laboratories  (2001):|"Product Information. Visken (pindolol)." Sandoz Pharmaceuticals Corporation  (2001):|"Product Information. Levatol (penbutolol)." Reed and Carnrick  (2001):|"Product Information. Coreg (carvedilol)." SmithKline Beecham  (2001):|"Product Information. Bystolic (nebivolol)." Forest Pharmaceuticals  (2007):', 'DDInter', 0);
