INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (10381, 8625, 'Nebivolol', 'Myasthenia Gravis', 'Beta-adrenergic receptor blocking agents (aka beta-blockers) may potentiate muscle weakness consistent with certain myasthenic symptoms such as diplopia, ptosis, and generalized weakness.  Several beta-blockers have been associated rarely with aggravation of muscle weakness in patients with preexisting myasthenia gravis or myasthenic symptoms.  Use cautiously in patients with myasthenia gravis.', '2', 'Confavreux C, Charles N, Aimard G "Fulminant myasthenia gravis soon after initiation of acebutolol therapy." Eur Neurol 30 (1990):  279-81|Berstein LP, Henkind P "Additional information on adverse reactions to timolol." Am J Ophthalmol 92 (1981):  295-6|Coppeto JR "Timolol-associated myasthenia gravis." Am J Ophthalmol 98 (1984):  244-5|Verkijk A "Worsening of myasthenia gravis with timolol maleate eyedrops." Ann Neurol 17 (1985):  211-2|Herishanu Y, Rosenberg P "Beta-blockers and myasthenia gravis." Ann Intern Med 83 (1975):  834-5|"Product Information. Blocadren (timolol)." Merck & Co., Inc  (2001):|Choi KL, Wat MS, Ip TP, Kung AWC, Lam KSL "Phaeochromocytoma associated with myasthenia gravis precipitated by propranolol treatment." Aust N Z J Med 25 (1995):  257', 'DDInter', 0);
