INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (11939, 741, 'Olmesartan', 'Liver Diseases', 'Olmesartan is eliminated unchanged in the urine and bile.  After repeated dosing in patients with severe renal impairment (CrCl < 20 mL/min), the systemic exposure (AUC) was approximately tripled compared to subjects with normal renal function.  In patients with moderate hepatic impairment, the AUC increased about 60% relative to that in matched controls.  The manufacturer states that no initial dosage adjustment is necessary in patients with moderate to marked renal or hepatic impairment.  However, patients should be monitored for undue adverse effects of the drug.', '2', '"Product Information. Benicar (olmesartan)." Sankyo Pharma  (2002):', 'DDInter', 0);
