INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (24122, 1325, 'Rosuvastatin', 'Hematuria', 'An association between rosuvastatin and the development of proteinuria and microscopic hematuria was observed among treated patients during clinical trials.  Patients with unexplained persistent proteinuria and/or hematuria during routine urinalysis testing should be instructed to reduce the dose of rosuvastatin according to clinical standards.  Therapy with rosuvastatin should be administered cautiously in patients showing abnormal urinalysis.  Monitoring for proteinuria and hematuria is recommended.', '2', '"Product Information. Crestor (rosuvastatin)." AstraZeneca Pharma Inc  (2003):', 'DDInter', 0);
