INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (24262, 3927, 'Ruxolitinib', 'Kidney Diseases', 'A dose reduction of ruxolitinib is recommended for patients with moderate or severe renal impairment, particularly those patients with myelofibrosis with a platelet count between 50 X 10^9/L and 150 X 10^9/L; and in patients presenting with end stage renal disease on dialysis.  Ruxolitinib is not removed by dialysis; however, the removal of some active metabolites by dialysis cannot be ruled out.', '2', '"Product Information. Jakafi (ruxolitinib)." Incyte Corporation  (2011):', 'DDInter', 0);
