INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (24652, 24582, 'Selegiline', 'Hypotension', 'Dopamine agonists may impair the systemic regulation of blood pressure, with resultant orthostatic hypotension, especially during dose escalation.  Therapy with dopamine agonists should be monitored carefully in patients with Parkinson''s disease since they may have an impaired ability to respond to an orthostatic challenge, and also in patients receiving antihypertensive drugs.', '3', 'Hoehn MM "Levodopa-induced postural hypotension. Treatment with  fludrocortisone." Arch Neurol 32 (1975):  50-1|Iwasaki S, Hamaguchi K, Iwasaki A, Takakusagi M, Narabayashi Y "Hypotensive effect of long-term levodopa in patients with Parkinson''s  disease." Eur Neurol 30 (1990):  194-9|"Product Information. Dostinex (cabergoline)." Pharmacia and Upjohn  (2001):|"Product Information. Sinemet (carbidopa-levodopa)." DuPont Pharmaceuticals  (2001):|"Product Information. Sinemet CR (carbidopa-levodopa)." DuPont Pharmaceuticals  (2001):|"Product Information. Mirapex (pramipexole)." Boehringer Ingelheim  (2001):|"Product Information. Requip (ropinirole)." SmithKline Beecham  (2001):|"Product Information. Comtan (entacapone)." Novartis Pharmaceuticals  (2001):|"Product Information. Azilect (rasagiline)." Teva Pharmaceuticals USA  (2006):|"Product Information. Neupro (rotigotine)." Schwarz Pharma  (2007):|"Product Information. Xadago (safinamide)." US WorldMeds LLC  (2017):', 'DDInter', 0);
