INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (24732, 281, 'Baricitinib', 'Kidney Diseases', 'Renal function significantly affects baricitinib exposure; renal elimination is the principal clearance mechanism for baricitinib through filtration and active secretion.  Baricitinib is not recommended in patients with rheumatoid arthritis or alopecia areata and severe renal dysfunction (estimated GFR [eGFR] less than 30 mL/min/1.73 m2); baricitinib is not recommended in patients with coronavirus disease 2019 (COVID-19) who are on dialysis, have ESRD, or have acute kidney injury (eGFR less than 15 mL/min/1.73 m2).  Dosage modifications are recommended for patients with rheumatoid arthritis or alopecia areata with moderate renal dysfunction (eGFR 30 to less than 60 mL/min/1.73 m2) and COVID-19 patients with moderate or severe renal dysfunction (eGFR 15 to less than 60 mL/min/1.73 m2); care should be exercised when using this agent in such patients.', '2', '"Product Information. Olumiant (baricitinib)." Lilly, Eli and Company  (2022):', 'DDInter', 0);
