INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (24772, 22831, 'Sevoflurane', 'Hyperkalemia', 'The use of inhaled anesthetic agents has been associated with rare increases in serum potassium levels that have resulted in cardiac arrhythmias and death in pediatric patients during the postoperative period.  Patients with latent as well as overt neuromuscular disease, particularly Duchenne muscular dystrophy, appear to be most vulnerable.  Early and aggressive intervention to treat the hyperkalemia and resistant arrhythmias is recommended; as is subsequent evaluation for latent neuromuscular disease.  Caution and close monitoring is recommended when using this agent in these patients.', '2', '"Product Information. Ultane (sevoflurane)." Abbott Pharmaceutical  (2001):', 'DDInter', 0);
