INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (24781, 22831, 'Sevoflurane', 'Seizures', 'The use of sevoflurane has been associated with seizures.  The majority of cases were in children and young adults, most of whom had no medical history of seizures.  Seizures have occurred during, or soon after sevoflurane induction, during emergence, and during post-operative recovery up to a day following anesthesia.  Caution and clinical judgment should be exercised when using sevoflurane in patients who may be at risk for seizures.', '2', '"Product Information. Ultane (sevoflurane)." Abbott Pharmaceutical  (2001):', 'DDInter', 0);
