INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (24971, 5732, 'Sertraline', 'Weight Loss', 'The use of selective serotonin reuptake inhibitors (SSRIs) may occasionally cause significant weight loss, which may be undesirable in patients suffering from anorexia, malnutrition or excessive weight loss.  Anorexia may occur in approximately 5% to 10% of patients.  Weight change should be monitored during therapy if an SSRI is used in these patients.', '1', 'Oliveros SC, Iruela LM, Caballero L, Baca E "Fluoxetine-induced anorexia in a bulimic patient." Am J Psychiatry 149 (1992):  1113-4|"Product Information. Zoloft (sertraline)." Roerig Division  (2001):|"Product Information. Prozac (fluoxetine)." Dista Products Company  (2001):|"Product Information. Paxil (paroxetine)." GlaxoSmithKline  (2001):|Vaz FJ, Salcedo MS "Fluoxetine-induced anorexia in a bulimic patient with antecedents of anorexia nervosa." J Clin Psychiatry 55 (1994):  118-9|Meyerowitz W, Jaramillo JDC "Sertraline treatment and weight loss." Curr Ther Res Clin Exp 55 (1994):  1176-81|Fernstrom MH, Massoudi M, Kupfer DJ "Fluvoxamine and weight loss." Biol Psychiatry 24 (1988):  948-9|Wagner W, Plekkenpol B, Gray TE, Vlaskamp H, Essers H "Review of fluvoxamine safety database." Drugs 43 Suppl 2 (1992):  48-53;disc. 53-4|"Product Information. Luvox (fluvoxamine)." Solvay Pharmaceuticals Inc  (2001):|Fichtner CG, Braum BG "Hyperphagia and weight loss during fluoxetine treatment." Ann Pharmacother 28 (1994):  1350-2|"Product Information. Celexa (citalopram)." Forest Pharmaceuticals  (2001):', 'DDInter', 0);
