INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (25574, 6304, 'Selumetinib', 'Hepatic Insufficiency', 'Selumetinib exposure is increased in patients with moderate or severe hepatic impairment.  A recommended dosage has not been established for patients with severe hepatic impairment (Child-Pugh C), and a dose reduction is required in patients with moderate hepatic impairment (Child-Pugh B).  Caution is advised when using in patients with these levels of hepatic impairment.', '2', '"Product Information. Koselugo (selumetinib)." Astra-Zeneca Pharmaceuticals  (2020):', 'DDInter', 0);
