INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (25630, 30881, 'Siponimod', 'Multiple Sclerosis', 'Rare cases of severe exacerbation of multiple sclerosis (MS), including disease rebound, have been reported after discontinuation of sphingosine 1-phosphate receptor modulator in MS treated patients.  The possibility of severe exacerbation of disease should be considered after stopping treatment with these agents.  Patients should be observed for a severe increase in disability upon discontinuation and appropriate treatment should be instituted, as required.', '2', '"Product Information. Gilenya (fingolimod)." Novartis Pharmaceuticals  (2010):|"Product Information. Mayzent (siponimod)." Novartis Pharmaceuticals  (2019):|"Product Information. Zeposia (ozanimod)." Celgene Corporation  (2020):|"Product Information. Ponvory (ponesimod)." Janssen Pharmaceuticals  (2021):', 'DDInter', 0);
