INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (25670, 687, 'Simvastatin', 'Kidney Diseases', 'Although simvastatin itself is not eliminated by the kidney, the plasma concentrations of total HMG-CoA reductase inhibitors after a single dose of simvastatin may be increased in patients with significant renal impairment, presumably due to the accumulation of active metabolites.  Increased HMG-CoA reductase inhibitory activity may be associated with a greater risk of adverse effects, including hepatic and musculoskeletal toxicities.  Therapy with simvastatin should be administered cautiously at a reduced dosage in patients with severe renal impairment.  Close clinical monitoring is recommended.', '3', 'Mauro VF "Clinical pharmacokinetics and practical applications of simvastatin." Clin Pharmacokinet 24 (1993):  195-202|"Product Information. Zocor (simvastatin)." Merck & Co., Inc  (2001):|Lennernas H, Fager G "Pharmacodynamics and pharmacokinetics of the HMG-CoA reductase inhibitors. Similarities and differences." Clin Pharmacokinet 32 (1997):  403-25', 'DDInter', 0);
