INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (25996, 213, 'Sodium cellulose phosphate', 'Bone Diseases', 'The use of cellulose sodium phosphate is contraindicated in patients with hyperparathyroidism (primary or secondary), bone disease (osteomalacia, osteoporosis), or low intestinal absorption and renal excretion of calcium.  Cellulose sodium phosphate prevents absorption of intestinal calcium and can further increase the risk of calcium adsorption and bone disease.', '3', '"Product Information. Calcibind (cellulose sodium phosphate)." Mission Pharmacal Company  (2001):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (25997, 213, 'Sodium cellulose phosphate', 'Water-Electrolyte Imbalance', 'The use of cellulose sodium phosphate is contraindicated in patients with undiagnosed hypophosphatemia (possible renal dysfunction or hypoparathyroidism), hypocalcemia (possible hypoparathyroidism, malabsorption syndrome), hypomagnesemia (further decrease may result in seizures), or hyperoxaluria (increased risk of renal oxalate stones).', '3', '"Product Information. Calcibind (cellulose sodium phosphate)." Mission Pharmacal Company  (2001):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (25998, 3367, 'Sodium iodide', 'Thyroid Hormone Metabolism, Abnormal', 'Elevated serum iodine levels can lead to altered thyroid function and metabolic abnormalities in patients receiving oral iodine therapy as well as topical therapy.  Iodine should be used cautiously in patients with thyroid disorders.  Monitoring thyroid function is recommended.', '2', '"Product Information. Betadine (povidone iodine topical)." Akorn Inc|"Product Information. Betadine (povidone iodine topical)." Purdue Frederick Company|"Product Information. Iodopen (sodium iodide)." Fujisawa  (2001):', 'DDInter', 0);
