INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (13321, 22141, 'Olaparib', 'Respiration Disorders', 'Pneumonitis, including fatal cases, occurred in <1% of patients treated with olaparib.  It is recommended to interrupt treatment if patients present new or worsening respiratory symptoms such as dyspnea, cough and fever, or a radiological abnormality.  Assess for the source of symptoms and if pneumonitis is confirmed, olaparib should be discontinued and the patient treated appropriately.', '2', '"Product Information. Lynparza (olaparib)." Astra-Zeneca Pharmaceuticals  (2014):', 'DDInter', 0);
