INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (26005, 6705, 'Sodium citrate', 'Water-Electrolyte Imbalance', 'Alkalinizing agents containing sodium can induce sodium and water retention and result in hypernatremia, hypokalemia, hyperosmolality, edema and aggravation of congestive heart failure.  Therapy with sodium- containing alkalinizing agents should not be used in patients with hypernatremia or fluid retention.  Clinical monitoring of acid/base balance and electrolytes prior to, during, a following completion of therapy with alkalinizing agents is recommended.', '3', '"Product Information. Sodium Bicarbonate (sodium bicarbonate)." Astra-Zeneca Pharmaceuticals  (2001):|"Product Information. Sodium Lactate (sodium lactate)." Abbott Pharmaceutical  (2001):', 'DDInter', 0);
