INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (26963, 0, 'Suvorexant', 'Depressive Disorder', 'A variety of abnormal thinking and behavior changes have been reported to occur in association with the use of most anxiolytics, sedatives and hypnotics.  Some of these changes include decreased inhibition, aggressiveness, agitation, and hallucinations.  These drugs can cause or exacerbate mental depression and cause suicidal behavior and ideation.  Therapy with these drugs should be administered cautiously in patients with a history of depression or other psychiatric disorders.  Patients should be monitored for any changes in mood or behavior.  It may be prudent to refrain from dispensing large quantities of medication to these patients.', '3', '"Product Information. Buspar (buspirone)." Bristol-Myers Squibb  (2002):|"Product Information. Ambien (zolpidem)." sanofi-aventis  (2001):|"Product Information. Placidyl (ethchlorvynol)." Abbott Pharmaceutical  (2001):|"Product Information. Aquachloral Supprettes (chloral hydrate)." Medisca Inc  (2001):|"Product Information. Equanil (meprobamate)." Wallace Laboratories  (2001):|"Product Information. Sonata (zaleplon)." Wyeth-Ayerst Laboratories  (2001):|"Product Information. Precedex (dexmedetomidine)." Abbott Pharmaceutical  (2001):|"Product Information. Xyrem (sodium oxybate)." Orphan Medical  (2002):|"Product Information. Lunesta (eszopiclone)." Sepracor Inc  (2004):|"Product Information. Rozerem (ramelteon)." Takeda Pharmaceuticals America  (2005):|"Product Information. Silenor (doxepin)." Somaxon Pharmaceuticals  (2010):|"Product Information. Unisom (doxylamine)." Pfizer U.S. Pharmaceuticals Group  (2013):|"Product Information. Hetlioz (tasimelteon)." Vanda Pharmaceuticals Inc  (2014):|"Product Information. Belsomra (suvorexant)." Merck & Co., Inc  (2014):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (26964, 0, 'Suvorexant', 'Kidney Failure, Chronic', 'Some anxiolytic, sedative and hypnotic drugs have not been studied in patients with severe renal impairment and should not be used on these patients.', '3', '"Product Information. Buspar (buspirone)." Bristol-Myers Squibb  (2002):|"Product Information. Sonata (zaleplon)." Wyeth-Ayerst Laboratories  (2001):|"Product Information. Rozerem (ramelteon)." Takeda Pharmaceuticals America  (2005):|"Product Information. Hetlioz (tasimelteon)." Vanda Pharmaceuticals Inc  (2014):|"Product Information. Belsomra (suvorexant)." Merck & Co., Inc  (2014):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (26965, 0, 'Suvorexant', 'Lung Diseases', 'Suvorexant has respiratory depressant effects and should be used with caution in patients with compromised respiratory function.  Suvorexant has not been studied in patients with severe sleep apnea and severe COPD and should not be used in these patients.', '3', '"Product Information. Belsomra (suvorexant)." Merck & Co., Inc  (2014):', 'DDInter', 0);
