INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (26975, 3617, 'Belladonna', 'Diabetic Neuropathies', 'Agents with anticholinergic activity can exacerbate many of the manifestations of autonomic neuropathy, including tachycardia, anhidrosis, bladder atony, obstipation, dry mouth and eyes, cycloplegia and blurring of vision, and sexual impotence in males.  Therapy with antimuscarinic agents and higher dosages of antispasmodic agents (e.g., dicyclomine or oxybutynin) should be administered cautiously in patients with autonomic neuropathy.', '3', '"Product Information. Atropine Sulfate (atropine)." ESI Lederle Generics  (2022):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (26976, 3617, 'Belladonna', 'Intestinal Obstruction', 'Anticholinergics are contraindicated in patients with obstructive diseases such as achalasia, esophageal stricture or stenosis, pyloroduodenal stenosis, stenosing peptic ulcer, pyloric obstruction, and paralytic ileus.  Anticholinergics may further suppress intestinal motility with resultant precipitation or aggravation of toxic megacolon.', '3', 'Bantz EW, Dolen WK, Chadwick EW, Nelson HS "Chronic chlorpheniramine therapy: subsensitivity, drug metabolism, and compliance." Ann Allergy 59 (1987):  341-6|Simons FE, Frith EM, Simons KJ "The pharmacokinetics and antihistaminic effects of brompheniramine." J Allergy Clin Immunol 70 (1982):  458-64|Blamoutier J "Comparative trial of two antihistamines, mequitazine and brompheniramine." Curr Med Res Opin 5 (1978):  366-70|"Azatadine (optimine)--a new antihistamine." Med Lett Drugs Ther 19 (1977):  77-9|"Product Information. Dimetane (brompheniramine)." Wyeth-Ayerst Laboratories  (2002):|"Product Information. Chlor-Trimeton (chlorpheniramine)." Schering-Plough|"Product Information. Periactin (cyproheptadine)." Merck & Co., Inc  (2002):|"Product Information. Benadryl (diphenhydramine)." Parke-Davis  (2002):|"Product Information. Phenergan (promethazine)." Wyeth-Ayerst Laboratories  (2001):|"Product Information. Tavist (clemastine)." Sandoz Pharmaceuticals Corporation  (2001):|"Product Information. Antivert (meclizine)." Roerig Division  (2001):|"Product Information. Optimine (azatadine)." Schering Corporation  (2001):|Mevorach D "Adverse effects of atropine sulfate autoinjection." Ann Pharmacother 26 (1992):  564|"Product Information. Atropine Sulfate (atropine)." ESI Lederle Generics  (2022):|"Product Information. Artane (trihexyphenidyl)." Lederle Laboratories  (2001):|"Product Information. Poly-Histine-D (pyrilamine)