INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (13857, 27865, 'Osimertinib', 'Eye Diseases', 'Some epidermal growth factor receptor (EGFR) inhibitors should be administered cautiously in patients with ocular disorders such as primary keratitis, ulcerative keratitis, or conjunctivitis.  It is recommended to interrupt or discontinue treatment with these agents if patients present with acute or worsening ocular disorders.', '3', '"Product Information. Iressa (gefitinib)." Astra-Zeneca Pharmaceuticals  (2003):|"Product Information. Erbitux (cetuximab)." Bristol-Myers Squibb  (2004):|"Product Information. Tarceva (erlotinib)." Genentech  (2004):|"Product Information. Vectibix (panitumumab)." Amgen USA  (2006):|"Product Information. Tykerb (lapatinib)." Novartis Pharmaceuticals  (2007):|"Product Information. Tagrisso (osimertinib)." Astra-Zeneca Pharmaceuticals  (2015):', 'DDInter', 0);
