INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (13863, 27865, 'Osimertinib', 'Hepatic Insufficiency', 'No formal pharmacokinetic studies of osimertinib have been conducted in patients with renal or hepatic impairment.  Based on population pharmacokinetic (PK) analysis of osimertinib, no dose adjustment is recommended in patients with mild (CrCl 60-89 mL/min) or moderate (CrCl 30-59 mL/min) renal impairment, or mild hepatic impairment (total bilirubin <ULN and AST between 1 to 1.5 times the ULN or total bilirubin between 1.0 to 1.5 times ULN and any AST ).  There are no data on the pharmacokinetics of osimertinib in patients with severe renal impairment (CrCl less than 30 mL/min) or with moderate to severe hepatic impairment (moderate:  total bilirubin between 1.5 to 3.0 times ULN and any AST, and severe:  total bilirubin between 3.0-10 times ULN and any AST).  Close monitoring is recommended for these patients.', '2', '"Product Information. Tagrisso (osimertinib)." Astra-Zeneca Pharmaceuticals  (2015):', 'DDInter', 0);
