INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (13885, 24385, 'Oxaprozin', 'Hypertension', 'Nonsteroidal anti-inflammatory drugs (NSAIDs), including topicals, can lead to new onset of hypertension or worsening of preexisting hypertension, either of which can contribute to the increased incidence of cardiovascular events.  NSAIDs should be used with caution in patients with hypertension.  Blood pressure should be monitored closely during the initiation of NSAID therapy and throughout the course of therapy.', '2', '"Product Information. Indocin (indomethacin)." Merck & Co., Inc  (2002):|"Product Information. Naprosyn (naproxen)." Syntex Laboratories Inc  (2002):|"Product Information. Voltaren (diclofenac)." Novartis Pharmaceuticals  (2001):|"Product Information. Relafen (nabumetone)." SmithKline Beecham  (2001):|"Product Information. Feldene (piroxicam)." Pfizer U.S. Pharmaceuticals  (2001):|"Product Information. Dolobid (diflunisal)." Merck & Co., Inc  (2001):|"Product Information. Ansaid (flurbiprofen)." Pharmacia and Upjohn  (2001):|"Product Information. Lodine (etodolac)." Wyeth-Ayerst Laboratories  (2001):|"Product Information. Daypro (oxaprozin)." Searle  (2001):|"Product Information. Celebrex (celecoxib)." Searle  (2001):|"Product Information. Meclofenamate Sodium (meclofenamate)." Mylan Pharmaceuticals Inc  (2012):|"Product Information. Flector Patch (diclofenac topical)." Actavis U.S. (Alpharma USPD)  (2016):', 'DDInter', 0);
