INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (28100, 9104, 'Timolol', 'Liver Diseases', 'Timolol is partially metabolized by the liver, and both parent drug and metabolites are eliminated by the kidney.  Patients with renal and/or liver disease may be at greater risk for adverse effects from timolol due to decreased drug clearance.  Therapy with timolol should be administered cautiously in patients with renal and/or liver dysfunction.  Dosage adjustments may be necessary.', '3', 'Lowenthal DT, Pitone JM, Affrime MB, et al. "Timolol kinetics in chronic renal insufficiency." Clin Pharmacol Ther 23 (1978):  606-15|McGourty JC, Silas JH, Fleming JJ, McBurney A, Ward JW "Pharmacokinetics and beta-blocking effects of timolol in poor and extensive metabolizers of debrisoquin." Clin Pharmacol Ther 38 (1985):  409-13|Dunn FG, Frohlich ED "Pharmacokinetics, mechanisms of action, indications, and adverse effects of timolol maleate, a nonselective beta-adrenoreceptor blocking agent." Pharmacotherapy 1 (1981):  188-200|"Product Information. Blocadren (timolol)." Merck & Co., Inc  (2001):', 'DDInter', 0);
