INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (28178, 28179, 'Ticlopidine', 'Hyperlipidemias', 'Ticlopidine can induce increases in serum cholesterol and triglycerides.  Serum total cholesterol levels have increased 8% to 10% in some patients within 1 month of initiation of ticlopidine therapy and have persisted at those levels.  Clinical monitoring of lipid profiles is recommended during ticlopidine therapy in patients at risk for hyperlipidemia-associated adverse events.', '2', '"Product Information. Ticlid (ticlopidine)." Syntex Laboratories Inc  (2001):', 'DDInter', 0);
