INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (28253, 2094, 'Tizanidine', 'Kidney Diseases', 'The clearance of tizanidine has been shown to reduce by more than 50% in elderly patients with renal impairment (creatinine clearance < 25 mL/min) compared to healthy elderly patients.  Prolonged pharmacologic effects of tizanidine may occur due to drug accumulation.  Therapy with tizanidine should be administered cautiously and initiated at reduced dosages in patients with impaired renal function.  During titration, individual doses rather than the dosing frequency should be increased.', '3', '"Product Information. Zanaflex (tizanidine)." Acorda Therapeutics  (2001):', 'DDInter', 0);
