INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (28429, 4982, 'Tofacitinib', 'Kidney Diseases', 'No dose adjustment of tofacitinib is needed in patients with mild renal dysfunction.  Tofacitinib-treated patients with moderate and severe renal dysfunction had greater tofacitinib blood levels than those with normal renal function; therefore, dose adjustment of tofacitinib is recommended in patients with moderate or severe renal dysfunction (including but not limited to those with severe dysfunction undergoing hemodialysis).  Caution should be exercised when using tofacitinib in these patients.', '2', '"Product Information. Xeljanz (tofacitinib)." Pfizer U.S. Pharmaceuticals Group  (2021):', 'DDInter', 0);
