INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (28823, 6268, 'Topotecan', 'Hemorrhagic Disorders', 'Topotecan induces myelosuppression.  Severe thrombocytopenia (< 25,000/mm3) has been reported during topotecan therapy.  Patients should be instructed to immediately report any signs or symptoms suggesting bleeding such as petechiae, purpura, hematuria, or melena.  Close clinical monitoring of hematopoietic function is recommended.  Therapy with topotecan should not be initiated in patients with platelet counts < 100,000/mm3.  Subsequent courses of topotecan should be withheld until platelet counts exceed 100,000/mm3.', '3', '"Product Information. Hycamtin (topotecan)." SmithKline Beecham  (2001):', 'DDInter', 0);
