INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (14525, 151, 'Oxytetracycline', 'Esophageal Diseases', 'The use of oral tetracycline capsules and tablets has been associated with esophageal irritation and ulceration in patients who ingested the drug without sufficient fluid shortly before bedtime.  Therapy with solid formulations of tetracyclines should preferably be avoided in patients with esophageal obstruction, compression or dyskinesia.  If the drugs are used, patients should be advised not to take the medication just before retiring and to drink fluids liberally.', '2', 'Aarons B, Bruns BJ "Oesophageal ulceration associated with ingestion of doxycycline." N Z Med J 91 (1980):  27|Geschwind A "Oesophagitis and oesophageal ulceration following ingestion of doxycycline tablets." Med J Aust 140 (1984):  223|Amendola MA, Spera TD "Doxycycline-induced esophagitis." JAMA 253 (1985):  1009-11|Khera DC, Herschman BR, Sosa F "Tetracycline-induced esophageal ulcers." Postgrad Med J 68 (1980):  113-5|Channer KS, Hollanders D "Tetracycline-induced oesophageal ulceration." Br Med J 282 (1981):  1359-60|"Product Information. Vibramycin (doxycycline)." Pfizer U.S. Pharmaceuticals  (2002):|"Product Information. Minocin (minocycline)." Lederle Laboratories  (2002):|"Product Information. Achromycin (tetracycline)." Lederle Laboratories  (2001):|"Product Information. Declomycin (demeclocycline)." Lederle Laboratories  (2001):|Foster JA, Sylvia LM "Doxycyline-induced esophageal ulceration." Ann Pharmacother 28 (1994):  1185-7|Nordt SP "Tetracycline-induced oral mucosal ulceration." Ann Pharmacother 30 (1996):  547-8|"Product Information. Terramycin (oxytetracycline)." Pfizer U.S. Pharmaceuticals  (2001):', 'DDInter', 0);
