INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (14620, 2994, 'Oxybutynin', 'Parkinson Disease', 'Oxybutynin is associated with anticholinergic central nervous system (CNS) effects.  If a patient experiences anticholinergic CNS effects, dose reduction or drug discontinuation should be considered.  Oxybutynin should be used with caution in patients with Parkinson''s disease and in patients with preexisting dementia treated with cholinesterase inhibitors due to the risk of aggravation of symptoms.', '2', '"Product Information. Ditropan (oxybutynin)." Hoechst Marion Roussel  (2001):', 'DDInter', 0);
