INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (14946, 0, 'Auranofin', 'Lupus Erythematosus, Systemic', 'The use of gold compounds is contraindicated in patients with systemic lupus erythematosus.  Prior to gold compounds therapy, a complete blood count with a differential, platelet count, urinalysis, and renal and liver function tests should be performed.  A complete blood count with a differential, platelet count, and urinalysis should be done at least monthly during therapy.', '3', '"Product Information. Ridaura (auranofin)." SmithKline Beecham  (2001):|"Product Information. Solganal (aurothioglucose)." Schering Corporation  (2001):|"Product Information. Myochrysine (gold sodium thiomalate)." Merck & Co., Inc  (2022):', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (14947, 29057, 'Panitumumab', 'Water-Electrolyte Imbalance', 'The use of certain EGFR inhibitors has been associated with progressive decreases in serum magnesium levels leading to severe cases of hypomagnesemia and accompanying electrolyte abnormalities, including hypocalcemia, and hypokalemia.  The onset of hypomagnesemia and accompanying electrolyte abnormalities occurred days to months after initiation of these agents.  Periodically monitor patients for hypomagnesemia, hypocalcemia, and hypokalemia, during and for at least 8 weeks following the completion of therapy.  Caution is recommended when using these agents in patients predisposed to electrolyte imbalances and those with a history of coronary artery disease, congestive heart failure, or arrhythmias.  Replete magnesium and other electrolytes as appropriate.', '3', '"Product Information. Erbitux (cetuximab)." Bristol-Myers Squibb  (2004):|"Product Information. Vectibix (panitumumab)." Amgen USA  (2006):|"Product Information. Portrazza (necitumumab)." Eli Lilly and Company  (2015):', 'DDInter', 0);
