INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (29240, 481, 'Tramadol', 'Epilepsy', 'Seizures have been reported in patients receiving tramadol within the recommended dosage range.  The risk appears to increase with doses above the recommended range and with concomitant use of certain drugs (e.g., those that reduce the seizure threshold).  Risk of seizure may also increase in patients with epilepsy, those with a history of seizures, or in patients with a recognized risk for seizure (e.g., head trauma, metabolic disorders, alcohol and drug withdrawal, CNS infections).  In tramadol overdose, naloxone administration may increase the risk of seizures.', '2', '"Product Information. Ultram (tramadol)." McNeil Pharmaceutical  (2001):', 'DDInter', 0);
