INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (29514, 11718, 'Trazodone', 'Seizures', 'The use of most antidepressants is associated with a risk of seizures.  There have been only rare reports of convulsions, including grand mal seizures, following the administration of nefazodone or trazodone.  Although a causal relationship has not been established, therapy with these agents should be administered cautiously in patients with a history of seizures.', '2', 'Bowdan ND "Seizure possibly caused by trazodone HCl." Am J Psychiatry 140 (1983):  642|Hohly EK, Martin RL "Increased seizure duration during ECT with trazodone administration." Am J Psychiatry 143 (1986):  1326|Lanes T, Ravaris CL "Prolonged ECT seizure duration in a patient taking trazodone." Am J Psychiatry 150 (1993):  525|Lefkowitz D, Kilgo G, Lee S "Seizures and trazodone therapy." Arch Gen Psychiatry 42 (1985):  523|Tasini M "Complex partial seizures in a patient receiving trazodone." J Clin Psychiatry 47 (1986):  318-9|Patel HC, Bruza D, Yeragani V "Myoclonus with trazodone." J Clin Psychopharmacol 8 (1988):  152|"Product Information. Desyrel (trazodone)." Bristol-Myers Squibb  (2001):|"Product Information. Serzone (nefazodone)." Bristol-Myers Squibb  (2001):', 'DDInter', 0);
