INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (29516, 11718, 'Trazodone', 'Depressive Disorder', 'Adults, young adults and children patients with depression and other psychiatric disorders may experience worsening of their symptoms and may have the emergence of suicidal thoughts and behavior.  Patients should be monitored appropriately and observed closely for worsening of their symptoms, suicidality or changes in their behavior, especially during the first few months of treatment, and at times of dose changes.  Discontinuing the medication should be considered if symptoms are persistently worse, or abrupt in onset.  Phenylpiperazine antidepressants are not approved for use in pediatric patients.', '2', '"Product Information. Desyrel (trazodone)." Bristol-Myers Squibb  (2001):|"Product Information. Serzone (nefazodone)." Bristol-Myers Squibb  (2001):', 'DDInter', 0);
