INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (29520, 11718, 'Trazodone', 'Hyponatremia', 'Treatment with trazodone may cause hyponatremia, in many cases secondary to development of the syndrome of inappropriate secretion of antidiuretic hormone (SIADH).  Caution should be used when treating patients at greater risk of developing hyponatremia such as elderly patients, patients taking diuretics or those who are volume-depleted.  Discontinuation of trazodone should be considered in patients with symptomatic hyponatremia and appropriate medical intervention should be instituted.', '2', '"Product Information. Desyrel (trazodone)." Bristol-Myers Squibb  (2001):', 'DDInter', 0);
