INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (29538, 28395, 'Trandolapril', 'Fibrosis', 'The clearance of trandolapril and its active metabolite, trandolaprilat, may be decreased in patients with hepatic cirrhosis.  Following oral administration in patients with mild to moderate alcoholic cirrhosis, plasma concentrations of trandolapril and trandolaprilat were 9-fold and 2-fold greater, respectively, than in normal subjects.  However, the inhibition of ACE activity was not affected.  The manufacturer states that lower initial dosages should be considered in patients with hepatic insufficiency.', '3', '"Product Information. Mavik (trandolapril)." Knoll Pharmaceutical Company  (2001):', 'DDInter', 0);
