INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (29659, 17154, 'Triptorelin', 'Diabetes Mellitus', 'Hyperglycemia and an increased risk of developing diabetes have been reported in men receiving GnRH agonists such as leuprolide and triptorelin.  Caution is advised in patients with diabetes as treatment with these agents may risk glycemic control.  Monitor blood glucose and/or HbA1c periodically in patients receiving treatment.', '2', '"Product Information. Trelstar (triptorelin)." Pharmacia and Upjohn  (2001):|"Product Information. Eligard (leuprolide)." Sanofi Winthrop Pharmaceuticals  (2002):', 'DDInter', 0);
