INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (29862, 3998, 'Upadacitinib', 'Neoplasms', 'Malignancies (including lymphomas and solid tumors) have been reported in patients treated with tofacitinib and other Janus kinase (JAK) inhibitors used to treat inflammatory conditions; lymphomas and other malignancies have been seen in patients treated with baricitinib or upadacitinib.  Patients who are current or past smokers are at additional increased risk of malignancies.  Based on a shared mechanism of action, this risk should be considered for other JAK inhibitors, including ruxolitinib, pacritinib, and fedratinib.  Before starting or continuing therapy, the benefits and risks for the individual patient should be considered, especially in patients with a known malignancy (other than successfully treated nonmelanoma skin cancer), patients who develop a malignancy during therapy, and patients who are current or past smokers.  Periodic skin examination is recommended for patients who are at increased risk for skin cancer.', '3', '"Product Information. Jakafi (ruxolitinib)." Incyte Corporation  (2011):|"Product Information. Olumiant (baricitinib)." Lilly, Eli and Company  (2018):|"Product Information. Rinvoq (upadacitinib)." AbbVie US LLC  (2019):|"Product Information. Inrebic (fedratinib)." Celgene Corporation  (2019):|"Product Information. Vonjo (pacritinib)." CTI BioPharma Corp.  (2022):|"Product Information. Xeljanz (tofacitinib)." Pfizer U.S. Pharmaceuticals Group  (2021):|"Product Information. Inrebic (fedratinib)." Bristol-Myers Squibb  (2022):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (29863, 31916, 'Upadacitinib', 'Neoplasms', 'Malignancies (including lymphomas and solid tumors) have been reported in patients treated with tofacitinib and other Janus kinase (JAK) inhibitors used to treat inflammatory conditions; lymphomas and other malignancies have been seen in patients treated with baricitinib or upadacitinib.  Patients who are current or past smokers are at additional increased risk of malignancies.  Based on a shared mechanism of action, this risk should be considered for other JAK inhibitors, including ruxolitinib, pacritinib, and fedratinib.  Before starting or continuing therapy, the benefits and risks for the individual patient should be considered, especially in patients with a known malignancy (other than successfully treated nonmelanoma skin cancer), patients who develop a malignancy during therapy, and patients who are current or past smokers.  Periodic skin examination is recommended for patients who are at increased risk for skin cancer.', '3', '"Product Information. Jakafi (ruxolitinib)." Incyte Corporation  (2011):|"Product Information. Olumiant (baricitinib)." Lilly, Eli and Company  (2018):|"Product Information. Rinvoq (upadacitinib)." AbbVie US LLC  (2019):|"Product Information. Inrebic (fedratinib)." Celgene Corporation  (2019):|"Product Information. Vonjo (pacritinib)." CTI BioPharma Corp.  (2022):|"Product Information. Xeljanz (tofacitinib)." Pfizer U.S. Pharmaceuticals Group  (2021):|"Product Information. Inrebic (fedratinib)." Bristol-Myers Squibb  (2022):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (29864, 3998, 'Upadacitinib', 'Thrombosis', 'Thrombosis (including deep venous thrombosis, pulmonary embolism, and arterial thrombosis) has occurred in patients treated for inflammatory conditions with Janus kinase (JAK) inhibitors, including baricitinib, tofacitinib, and upadacitinib; many of these adverse events were serious and some resulted in death.  Based on a shared mechanism of action, this risk should be considered for other JAK inhibitors, including ruxolitinib, fedratinib, and pacritinib.  Baricitinib, pacritinib, tofacitinib, and upadacitinib should be avoided in patients who may be at increased risk of thrombosis; for the treatment of ulcerative colitis, tofacitinib should be used at the lowest effective dose and for the shortest duration needed to achieve/maintain therapeutic response.  If symptoms of thrombosis occur, baricitinib, pacritinib, tofacitinib, and upadacitinib should be discontinued and patients should be evaluated promptly and treated appropriately.', '3', '"Product Information. Jakafi (ruxolitinib)." Incyte Corporation  (2011):|"Product Information. Olumiant (baricitinib)." Lilly, Eli and Company  (2018):|"Product Information. Rinvoq (upadacitinib)." AbbVie US LLC  (2019):|"Product Information. Inrebic (fedratinib)." Celgene Corporation  (2019):|"Product Information. Vonjo (pacritinib)." CTI BioPharma Corp.  (2022):|"Product Information. Xeljanz (tofacitinib)." Pfizer U.S. Pharmaceuticals Group  (2021):|"Product Information. Inrebic (fedratinib)." Bristol-Myers Squibb  (2022):', 'DDInter', 0);
