INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (29868, 3998, 'Upadacitinib', 'Pancytopenia', 'The use of upadacitinib is associated with an increase incidence of neutropenia, lymphopenia, and anemia.  Avoid initiation or interrupt treatment with upadacitinib in patients with low neutrophil counts (i.e., ANC less than 1000 cells/mm3), lymphocyte counts (i.e., less than 500 cells/mm3) and low hemoglobin levels (i.e., less than 8 g/dL).  It is recommended to evaluate patients for abnormal blood cell counts at baseline and thereafter according to routine patient management.', '2', '"Product Information. Rinvoq (upadacitinib)." AbbVie US LLC  (2019):', 'DDInter', 0);
