INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (29874, 3998, 'Upadacitinib', 'Hyperlipidemias', 'Treatment with upadacitinib is associated with increases in lipid parameters, including total cholesterol, low-density lipoprotein (LDL) cholesterol, and high-density lipoprotein (HDL) cholesterol.  Care should be exercised when using this agent in patients with hyperlipidemia.  It is recommended to monitor patients after initiation of treatment, and thereafter according to the clinical guidelines and to manage patients accordingly.', '2', '"Product Information. Rinvoq (upadacitinib)." AbbVie US LLC  (2019):', 'DDInter', 0);
