INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (29891, 15466, 'Urofollitropin', 'Hypothyroidism', 'The use of follicle stimulating hormone (FSH) is contraindicated in patients with uncontrolled thyroid dysfunction.  FSH is composed of both an alpha and beta subunit.  The amino acid sequence of the alpha subunit of FSH is identical to that of TSH.  Therefore, FSH may have weak TSH receptor- binding and activation.', '3', '"Product Information. Fertinex (urofollitropin)." Serono Laboratories Inc|"Product Information. Follistim (follicle stimulating hormone)." Organon  (2002):|"Product Information. Gonal-F (follicle stimulating hormone)." Serono Laboratories Inc  (2002):|"Product Information. Bravelle (urofollitropin)." Ferring Pharmaceuticals Inc  (2003):', 'DDInter', 0);
