INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (29895, 15466, 'Urofollitropin', 'Neoplasms', 'The use of follicle stimulating hormone is contraindicated in patients with sex hormone-dependent tumors of the reproductive tract and accessory organs (ovary, breast, uterus) and in patients with pituitary tumors.  FSH is required for normal sex hormone production, which may be detrimental in these patients.  Patients in later reproductive life have a greater predisposition to endometrial carcinoma.', '3', '"Product Information. Fertinex (urofollitropin)." Serono Laboratories Inc|"Product Information. Follistim (follicle stimulating hormone)." Organon  (2002):|"Product Information. Gonal-F (follicle stimulating hormone)." Serono Laboratories Inc  (2002):|"Product Information. Bravelle (urofollitropin)." Ferring Pharmaceuticals Inc  (2003):', 'DDInter', 0);
