INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (29923, 27493, 'Ustekinumab', 'Infections', 'There have been reports of serious infections, including infections with opportunistic pathogens, and reactivation of latent infections in patients receiving interleukin inhibitors.  Treatment with these agents should not be initiated in patients with an active infection until the infection resolves or is adequately treated.  Caution is recommended when considering the use of interleukin inhibitors in patients with a history of recurrent infections, underlying conditions which may predispose them to infections, or chronic, latent, or localized infections.', '2', '"Product Information. Stelara (ustekinumab)." Centocor Inc  (2009):|"Product Information. Ilaris (canakinumab)." Novartis Pharmaceuticals  (2009):|"Product Information. Sylvant (siltuximab)." Janssen Biotech, Inc.  (2014):|"Product Information. Cosentyx (secukinumab)." Novartis Pharmaceuticals  (2015):|"Product Information. Nucala (mepolizumab)." GlaxoSmithKline  (2015):|"Product Information. Cinqair (reslizumab)." Teva Pharmaceuticals USA  (2016):|"Product Information. Tremfya (guselkumab)." Janssen Biotech, Inc.  (2017):', 'DDInter', 0);
