INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (29929, 0, 'Valaciclovir', 'Dehydration', 'Valacyclovir is almost completely converted to acyclovir and L-valine by first-pass intestinal and/or hepatic metabolism; acyclovir is primarily eliminated by the kidney.  Cases of acute renal failure have been reported in patients without adequate hydration.  Acyclovir may precipitate in renal tubules when the solubility (2.5 mg/mL) is exceeded in the intratubular fluid.  Adequate hydration should be maintained for all patients.  If acute renal failure and anuria occur, patients may benefit from hemodialysis until renal function is restored.', '2', '"Product Information. Valtrex (valacyclovir)." Glaxo Wellcome  (2001):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (29930, 0, 'Valaciclovir', 'Diseases requiring hemodialysis', 'Valacyclovir is almost completely converted to acyclovir and L-valine by first-pass intestinal and/or hepatic metabolism.  About one-third of acyclovir in the body is removed by dialysis during a 4-hour hemodialysis session.  The recommended dose of valacyclovir should be administered after hemodialysis.', '2', '"Product Information. Valtrex (valacyclovir)." Glaxo Wellcome  (2001):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (29931, 0, 'Valaciclovir', 'Kidney Diseases', 'Valacyclovir is almost completely converted to acyclovir and L-valine by first-pass intestinal and/or hepatic metabolism; acyclovir is primarily eliminated by the kidney.  Cases of acute renal failure and CNS side effects (including agitation, hallucinations, confusion, delirium, seizures, and encephalopathy) have been reported in patients with preexisting renal disease who received higher-than-recommended doses of valacyclovir for their level of renal function.  Cases of acute renal failure have also been reported in patients receiving other nephrotoxic agents.  Therapy with valacyclovir should be administered cautiously in elderly patients (with or without renal dysfunction) and patients receiving other nephrotoxic agents.  Dosage reductions are recommended in patients with renal dysfunction.  If acute renal failure and anuria occur, patients may benefit from hemodialysis until renal function is restored; valacyclovir should be discontinued if CNS side effects develop.', '2', '"Product Information. Valtrex (valacyclovir)." Glaxo Wellcome  (2001):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (29932, 0, 'Valaciclovir', 'Purpura, Thrombotic Thrombocytopenic', 'Thrombotic thrombocytopenic purpura/hemolytic uremia syndrome (TTP/HUS), in some cases resulting in death, has been reported in patients with advanced HIV-1 disease, in allogeneic bone marrow transplant recipients, and in renal transplant recipients during clinical trials of valacyclovir at doses of 8 g/day.  Therapy with valacyclovir should be discontinued immediately if clinical signs, symptoms, and laboratory abnormalities consistent with TTP/HUS develop.', '2', '"Product Information. Valtrex (valacyclovir)." Glaxo Wellcome  (2001):', 'DDInter', 0);
