INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (15166, 9657, 'Paroxetine', 'Kidney Diseases', 'Paroxetine may accumulate in renal dysfunction.  The mean plasma concentration of paroxetine in patients with creatinine clearance below 30 mL/min has been shown to be approximately four times greater than that in normal patients.  Therapy with paroxetine should be administered cautiously in patients with renal impairment.  A reduction in the daily dosage is recommended if renal function is severely impaired.', '2', 'Doyle GD, Laher M, Kelly JG, Byrne MM, Clarkson A, Zussman BD "The pharmacokinetics of paroxetine in renal impairment." Acta Psychiatr Scand Suppl 350 (1989):  89-90|Kaye CM, Haddock RE, Langley PF, Mellows G, Tasker TC, Zussman BD, Greb WH "A review of the metabolism and pharmacokinetics of paroxetine in man." Acta Psychiatr Scand Suppl 350 (1989):  60-75|"Product Information. Paxil (paroxetine)." GlaxoSmithKline  (2001):', 'DDInter', 0);
