INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (15296, 26948, 'Pasireotide', 'Adrenal Insufficiency', 'Treatment with somatostatin analogs may suppress the pituitary hormones other than GH/IGF-1.  It is recommended to monitor pituitary function prior to initiation of therapy and periodically during treatment, as clinically appropriate.  Patients should be monitored for and instructed on the signs and symptoms of adrenal insufficiency during therapy.  If adrenal insufficiency is suspected it should be confirmed and treated per standard of care with exogenous glucocorticoids at replacement doses.', '2', '"Product Information. Somatuline Depot (lanreotide)." Ipsen Inc  (2007):|"Product Information. Signifor (pasireotide)." Novartis Pharmaceuticals  (2013):', 'DDInter', 0);
