INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (15326, 0, 'Acetaminophen', 'Phenylketonurias', 'Several oral acetaminophen and acetaminophen-combination products, particularly flavored chewable tablets, contain the artificial sweetener, aspartame (NutraSweet).  Aspartame is converted to phenylalanine in the gastrointestinal tract following ingestion.  Chewable and effervescent formulations of acetaminophen products may also contain phenylalanine.  The aspartame/phenylalanine content should be considered when these products are used in patients who must restrict their intake of phenylalanine (i.e. phenylketonurics).', '2', '"Product Information. Tylenol (acetaminophen)." McNeil Pharmaceutical  (2002):', 'DDInter', 0);
