INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (32554, 1970, 'Bevacizumab', 'Hypertension', 'Inhibitors of vascular endothelial growth factor receptors (VEGFR) have been associated with severe increases in blood pressure.  Therapy with these agents should be administered cautiously in patients with preexisting hypertension.  Blood pressure should be assessed prior to initiating treatment, regularly during treatment and after discontinuation in patients with bevacizumab -induced or -exacerbated hypertension.  Temporarily suspend these agents in patients with severe hypertension that is not controlled with medical management.  Discontinue these agents in patients with hypertensive crisis or hypertensive encephalopathy.', '2', '"Product Information. Avastin (bevacizumab)." Genentech  (2004):|"Product Information. Votrient (pazopanib)." GlaxoSmithKline  (2009):|"Product Information. Cyramza (ramucirumab)." Eli Lilly and Company  (2014):', 'DDInter', 0);
