INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (32749, 222, 'Acetylsalicylic acid', 'Glucosephosphate Dehydrogenase Deficiency', 'Salicylates, particularly aspirin, may cause or aggravate hemolysis in patients with pyruvate kinase or glucose-6-phosphate dehydrogenase (G-6-PD) deficiency.  However, this effect has not been clearly established.  Until more data are available, therapy with salicylates should be administered cautiously in patients with G-6-PD deficiency.  The same precaution should also be observed with the use of related agents such as salicylamide because of their structural and pharmacological similarities to salicylate.', '2', '"Product Information. Salflex (salsalate)." Carnrick Laboratories Inc  (2001):|"Product Information. Ecotrin (aspirin)." SmithKline Beecham  (2001):|"Product Information. Rexolate (sodium thiosalicylate)." Hyrex Pharmaceuticals  (2001):', 'DDInter', 0);
