INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (32981, 3933, 'Botulinum toxin type A', 'Glaucoma, Angle-Closure', 'Xeomin (brand of incobotulinumtoxinA), a botulinum toxin product for the treatment of blepharospasm should be used with caution in patients at risk of developing narrow angle glaucoma.  The anticholinergic effects of botulinum toxin products can reduce blinking and lead to corneal exposure, persistent epithelial defect and corneal ulceration, especially in patients with VII nerve disorders.  To prevent ectropion, it is recommended that botulinum toxin products should not be injected into the medial lower eyelid area.', '2', '"Product Information. Xeomin (incobotulinumtoxinA)." Merz Pharmaceuticals  (2022):', 'DDInter', 0);
