INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (16638, 29286, 'Azacitidine', 'Liver Neoplasms', 'The use of azacitidine injection is contraindicated in patients with advanced malignant hepatic tumors.', '3', '"Product Information. Vidaza (azacitidine)." Celgene Corporation  (2004):', 'DDInter', 0);
