INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (16640, 29286, 'Azacitidine', 'Kidney Diseases', 'Patients with renal impairment may be at increased risk of renal toxicity when using azacitidine.  This drug is primarily excreted by the kidneys.  Therefore, these patients should be closely monitored for toxicity.', '2', '"Product Information. Vidaza (azacitidine)." Celgene Corporation  (2004):', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (16641, 29286, 'Azacitidine', 'Anemia', 'Some miscellaneous antineoplastics such as azacitidine and lenalidomide cause anemia, neutropenia, and thrombocytopenia.  Patients with these preexisting conditions should be monitored carefully.  Monitor complete blood counts frequently for response and/or toxicity, at a minimum, prior to each dosing cycle.', '2', '"Product Information. Vidaza (azacitidine)." Celgene Corporation  (2004):|"Product Information. Revlimid (lenalidomide)." Celgene Corporation  (2006):', 'DDInter', 0);
